import React, { useState } from 'react';
import './ServicesTable.css'; // CSS file for user table styling

const ServicesTable = ({ users, heading, buttonLabel }) => {
  const [searchTerm, setSearchTerm] = useState('');

  // Filter users based on the search term
  const filteredUsers = users.filter((user) =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="user-table-container">
      <div className="header">
        <h2>{heading}</h2> {/* Dynamic heading */}
        <input
          type="text"
          placeholder="Search by name"
          value={searchTerm}
          onChange={handleSearch}
          className="search-bar"
        />
      </div>
      <table className="user-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Total Users</th>
            <th></th> {/* For the action button */}
          </tr>
        </thead>
        <tbody>
          {filteredUsers.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.description}</td>
              <td>{user.users}</td>
              <td>
                {/* <button className={buttonLabel === 'Activate' ? 'activate-btn' : 'deactivate-btn'}>
                  {buttonLabel}
                </button> */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Remove the pagination section */}
    </div>
  );
};

export default ServicesTable;
