// export default Dashboard;
import React, { useState } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import UserTable from '../components/UserTable';
import './Dashboard.css'; // CSS file for dashboard styling
const Dashboard = () => {
  const [currentView, setCurrentView] = useState('Active Users'); // State to manage the current view

  const activeUsers = [
    { name: 'John Doe', email: 'john@example.com', number: '1234567890', profession: 'Engineer', status: 'Active', role: 'Admin' },
    { name: 'Jane Smith', email: 'jane@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    // More active user data...
  ];

  const inactiveUsers = [
    { name: 'Inactive User 1', email: 'inactive1@example.com', number: '1111111111', profession: 'Doctor', status: 'Inactive', role: 'User' },
    { name: 'Inactive User 2', email: 'inactive2@example.com', number: '2222222222', profession: 'Lawyer', status: 'Inactive', role: 'User' },
    // More inactive user data...
  ];

  const consultants = [
    { name: 'Alice Johnson', email: 'alice@example.com', number: '2345678901', profession: 'Consultant', status: 'Active', role: 'Consultant' },
    { name: 'Bob Brown', email: 'bob@example.com', number: '3456789012', profession: 'Consultant', status: 'Active', role: 'Consultant' },
    // More consultant data...
  ];

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  const getViewData = () => {
    switch (currentView) {
      case 'Consultants':
        return { users: consultants, heading: 'All Consultants', buttonLabel: 'Deactivate' };
      case 'Inactive Users':
        return { users: inactiveUsers, heading: 'All Inactive Users', buttonLabel: 'Activate' };
      default:
        return { users: activeUsers, heading: 'All Active Users', buttonLabel: 'Deactivate' };
    }
  };

  const { users, heading, buttonLabel } = getViewData();

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleViewChange} />
      <div className="content">
        <div className="stat-cards">
          <StatCard title="Total Active Users" value="1,234" />
          <StatCard title="Total Inactive Users" value="5353" />
          <StatCard title="Total Consultants" value="1232" />
          <StatCard title="Total Customers" value="1243" />
        </div>
        <UserTable 
          users={users} 
          heading={heading}
          buttonLabel={buttonLabel}
        />
      </div>
    </div>
  );
};


export default Dashboard;
