// export default Dashboard;
import React, { useState } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import UserTable from '../components/UserTable';
import './Dashboard.css'; // CSS file for dashboard styling

const Dashboard = () => {
  const [currentView, setCurrentView] = useState('Active Users'); // State to manage the current view

  const users = [
    { name: 'John Doe', email: 'john@example.com', number: '1234567890', profession: 'Engineer', status: 'Active', role: 'Admin' },
    { name: 'Jane Smith', email: 'jane@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    { name: 'John Doe', email: 'john@example.com', number: '1234567890', profession: 'Engineer', status: 'Active', role: 'Admin' },
    { name: 'Jane Smith', email: 'jane@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    { name: 'John Doe', email: 'john@example.com', number: '1234567890', profession: 'Engineer', status: 'Active', role: 'Admin' },
    { name: 'faizy', email: 'jane@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    // Add more user data here
  ];

  const consultants = [
    { name: 'Alice Johnson', email: 'alice@example.com', number: '2345678901', profession: 'Consultant', status: 'Active', role: 'Consultant' },
    { name: 'Bob Brown', email: 'bob@example.com', number: '3456789012', profession: 'Consultant', status: 'Active', role: 'Consultant' },
    // Add more consultant data here
  ];

  const heading = currentView === 'Active Users' ? 'All Active Users' : 
                  currentView === 'Inactive Users' ? 'All Inactive Users' : 
                  'Edit Profile';
  const buttonLabel = currentView === 'Active Users' ? 'Deactivate' : 'Activate';

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  const renderContent = () => {
    if (currentView === 'Edit Profile') {
      return <EditProfileComponent />;
    } else {
      return (
        <UserTable 
          users={users} 
          heading={heading}
          buttonLabel={buttonLabel}
        />
      );
    }
  };

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleViewChange} />
      <div className="content">
        <div className="stat-cards">
          <StatCard title="Total Active Users" value="1,234" />
          <StatCard title="Total Inactive Users" value="5,353" />
          <StatCard title="Total Consultants" value="1,232" />
          <StatCard title="Total Customers" value="1,243" />
        </div>
        {renderContent()}
      </div>
    </div>
  );
};

export default Dashboard;
