import React, { useState, useEffect } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import UserTable from '../components/UserTable';
import './Consultants.css'; // CSS file for dashboard styling

const Consultants = () => {
  const [currentView, setCurrentView] = useState('Consultants'); // State to manage the current view
  const [consultants, setConsultants] = useState([]); // State to store consultants fetched from the API
  const [loading, setLoading] = useState(true); // State to manage loading status

  useEffect(() => {
    const fetchConsultants = async () => {
      try {
        const response = await fetch('http://localhost:3000/consultants');
        const data = await response.json();

        // Format the response data if needed
        const formattedConsultants = data.map(user => ({
          name: user.name,
          email: user.email,
          number: user.phoneNumber,
          profession: user.profession || 'N/A',
          status: user.accDeactivated ? 'Inactive' : 'Active',
          role: user.role
        }));

        setConsultants(formattedConsultants);
        setLoading(false); // Stop loading
      } catch (error) {
        console.error("Error fetching consultants:", error);
        setLoading(false); // Stop loading even if there's an error
      }
    };

    fetchConsultants();
  }, []);

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleViewChange} />
      <div className="content">
        <div className="stat-cards">
       
        </div>
        {loading ? (
          <p>Loading consultants...</p>
        ) : (
          <UserTable 
            users={consultants} 
            heading="All Consultants"
            buttonLabel="Delete"
          />
        )}
      </div>
    </div>
  );
};

export default Consultants;
