// export default Dashboard;
import React, { useState, useEffect } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import UserTable from '../components/UserTable';
import './Dashboard.css'; // CSS file for dashboard styling

const Dashboard = () => {
  const [currentView, setCurrentView] = useState('Active Users'); // State to manage the current view
  const [users, setUsers] = useState([]); // State to store users fetched from the API

  useEffect(() => {
    const fetchActiveAccounts = async () => {
      try {
        const response = await fetch('http://localhost:3000/activeAccounts');
        const data = await response.json();
        
        // Filter and map the data to match the fields required for UserTable
        const formattedUsers = data
          .filter(user => user.conAccApproved) // Only include active users
          .map(user => ({
            name: user.name,
            email: user.email,
            number: user.phoneNumber,
            profession: user.profession || 'N/A', // Handle missing profession
            status: user.conAccApproved ? 'Active' : 'Inactive',
            role: user.role,
          }));

        setUsers(formattedUsers);
      } catch (error) {
        console.error('Error fetching active accounts:', error);
      }
    };

    fetchActiveAccounts();
  }, [currentView]);

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleViewChange} />
      <div className="content">
        <div className="stat-cards">
          <StatCard title="Total Active Users" value={users.length} />
          {/* Other StatCards can be added similarly */}
        </div>
        <UserTable 
          users={users} 
          heading={
            currentView === 'Consultants' 
              ? 'All Consultants' 
              : currentView === 'Inactive Users' 
                ? 'All Inactive Users' 
                : 'All Active Users'
          }
          buttonLabel={
            currentView === 'Inactive Users' 
              ? 'Activate' 
              : 'Deactivate'
          }
        />
      </div>
    </div>
  );
};

export default Dashboard;
