import React, { useState } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import UserTable from '../components/UserTable';
import './InactiveUser.css'; // CSS file for dashboard styling

const InactiveUsers = () => {
  const [currentView, setCurrentView] = useState('Inactive Users'); // State to manage the current view

  const inactiveUsers = [
    { name: 'Faizy 0', email: 'faizy0@example.com', number: '1234567890', profession: 'Engineer', status: 'Inactive', role: 'Admin' },
    { name: 'Faizy 1', email: 'faizy1@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    { name: 'Faizy 2', email: 'faizy2@example.com', number: '1234567890', profession: 'Engineer', status: 'Inactive', role: 'Admin' },
    { name: 'Faizy 3', email: 'faizy3@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    { name: 'Faizy 4', email: 'faizy4@example.com', number: '1234567890', profession: 'Engineer', status: 'Inactive', role: 'Admin' },
    { name: 'Faizy 5', email: 'faizy5@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    // Add more user data here
  ];

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleViewChange} />
      <div className="content">
        <div className="stat-cards">
          <StatCard title="Total Active Users" value="1,234" />
          <StatCard title="Total Inactive Users" value="5,353" />
          <StatCard title="Total Consultants" value="1,232" />
          <StatCard title="Total Customers" value="1,243" />
        </div>
        <UserTable 
          users={inactiveUsers} 
          heading="All Inactive Users"
          buttonLabel="Activate"
        />
      </div>
    </div>
  );
};

export default InactiveUsers;
