import React, { useState, useEffect } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import PaymentsTable from '../components/PaymentsTable';
import './Services.css'; // Optional CSS file for services styling
import './Dashboard.css'; // Assuming this CSS file contains styles for stat cards, etc.

const Payments = () => {
  const [payments, setPayments] = useState([]); // State to store payments data
  const [loading, setLoading] = useState(true); // State to manage loading status
  const [currentPage, setCurrentPage] = useState(1); // Current page for pagination
  const [totalPages, setTotalPages] = useState(1); // Total number of pages for pagination

  const fetchPayments = async (page = 1) => {
    try {
      const response = await fetch(`http://localhost:3000/usersWithBalance?page=${page}`);
      const data = await response.json();

      // Assuming API returns total number of pages and data array
      setPayments(data.items); // Adjust according to your API response
      setTotalPages(data.totalPages); // Adjust according to your API response
      setLoading(false); // Stop loading
    } catch (error) {
      console.error("Error fetching payments:", error);
      setLoading(false); // Stop loading even if there's an error
    }
  };

  useEffect(() => {
    fetchPayments(currentPage);
  }, [currentPage]);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="services">
      <Sidebar />
      <div className="content">
        <div className="stat-cards">
          <StatCard title="Total Active Users" value="1,234" />
          <StatCard title="Total Inactive Users" value="5,353" />
          <StatCard title="Total Consultants" value="1,232" />
          <StatCard title="Total Customers" value="1,243" />
        </div>
        {loading ? (
          <p>Loading payments...</p>
        ) : (
          <PaymentsTable
            users={payments}
            heading="All Payments"
            buttonLabel="Deactivate"
       
          />
        )}
      </div>
    </div>
  );
};

export default Payments;
