import React, { useState, useEffect } from 'react';
import Sidebar from '../components/sidebar';
import StatCard from '../components/statcard';
import UserTable from '../components/UserTable';
import './InactiveUser.css'; // CSS file for dashboard styling

const InactiveUsers = () => {
  const [currentView, setCurrentView] = useState('Inactive Users');
  const [inactiveUsers, setInactiveUsers] = useState([]);
  const [totalInactiveUsers, setTotalInactiveUsers] = useState(0);

  useEffect(() => {
    const fetchInactiveAccounts = async () => {
      try {
        const response = await fetch('http://localhost:3000/inactiveAccounts');
        const data = await response.json();
        const formattedUsers = data.map(user => ({
          name: user.name || 'N/A',
          email: user.email,
          number: user.phoneNumber || 'N/A',
          profession: user.profession || 'N/A',
          status: user.conAccApproved ? 'Active' : 'Inactive',
          role: user.role,
        }));
        setInactiveUsers(formattedUsers);
      } catch (error) {
        console.error('Error fetching inactive accounts:', error);
      }
    };

    const fetchTotalInactiveUsers = async () => {
      try {
        const response = await fetch('http://localhost:3000/totalInactiveUsers');
        const data = await response.json();
        setTotalInactiveUsers(data.totalInactiveUsers);
      } catch (error) {
        console.error('Error fetching total inactive users:', error);
      }
    };

    fetchInactiveAccounts();
    fetchTotalInactiveUsers();
  }, [currentView]);

  const handleViewChange = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="dashboard">
      <Sidebar onMenuClick={handleViewChange} />
      <div className="content">
        <div className="stat-cards">
          <StatCard title="Total Active Users" value="1,234" />
          <StatCard title="Total Inactive Users" value={totalInactiveUsers} />
          <StatCard title="Total Consultants" value="1,232" />
          <StatCard title="Total Customers" value="1,243" />
        </div>
        <UserTable 
          users={inactiveUsers} 
          heading="All Inactive Users"
          buttonLabel="Activate"
        />
      </div>
    </div>
  );
};

export default InactiveUsers;
