// export default Dashboard;
import React from 'react';
import Sidebar from '../components/sidebar'; // Import your Sidebar component
import StatCards from '../components/statcard'; // Import your StatCards component
import UserTable from '../components/UserTable'; // Import your UserTable component

const Dashboard = () => {
  // Sample user data
  const users = [
    { name: 'John Doe', email: 'john@example.com', number: '1234567890', profession: 'Developer', status: 'Active', role: 'Admin' },
    { name: 'Jane Smith', email: 'jane@example.com', number: '0987654321', profession: 'Designer', status: 'Inactive', role: 'User' },
    { name: 'Alice Johnson', email: 'alice@example.com', number: '1122334455', profession: 'Manager', status: 'Active', role: 'Manager' },
  ];

  // Filter active users
  const activeUsers = users.filter(user => user.status === 'Active');

  return (
    <div className="dashboard">
      <Sidebar />
      <div className="main-content">
        <StatCards />
        <UserTable users={activeUsers} />
      </div>
    </div>
  );
};

export default Dashboard;
